import React from "react";
import { useSelector } from "react-redux";
import { selectQuizzes } from "../quizzes/quizzesSlice"; // Adjust import path if necessary

export default function Topic() {
  const quizzes = useSelector(selectQuizzes); // Use the selector to get quizzes
  // Assuming you have a way to identify the current topic

  return (
    <div>
      <h1>Topic</h1>
      {quizzes.map((quiz) => (
        <div key={quiz.id}>
          <h2>{quiz.name}</h2>
          {/* Render other quiz details here */}
        </div>
      ))}
    </div>
  );
}
