import { configureStore } from "@reduxjs/toolkit";
import topicsReducer from "../features/topics/topics"; // Ensure this is the correct import path
import quizzesReducer from "../features/quizzes/quizzesSlice"; // Import the quizzes reducer

export default configureStore({
  reducer: {
    topics: topicsReducer, // Add the topics reducer
    quizzes: quizzesReducer, // Add the quizzes reducer
  },
});
