import React from "react";
import { useSelector } from "react-redux";
import { Link } from "react-router-dom";
import ROUTES from "../../app/routes";
// Import the selector for quizzes
import { selectQuizzes } from "../../features/quizzes/quizzesSlice"; 

export default function Quizzes() {
  // Use the selector to get all quizzes from the state
  const quizzes = useSelector(selectQuizzes);

  return (
    <section className="center">
      <h1>Quizzes</h1>
      <ul className="quizzes-list">
        {Object.values(quizzes).length === 0 ? ( // Check if quizzes array is empty
          <p>No quizzes available.</p>
        ) : (
          Object.values(quizzes).map((quiz) => (
            <li className="quiz" key={quiz.id}> {/* Moved the key prop here */}
              <Link to={ROUTES.quizRoute(quiz.id)}>{quiz.name}</Link> {/* Link wraps quiz name */}
            </li>
          ))
        )}
      </ul>
      <Link to={ROUTES.newQuizRoute()} className="button">
        Create New Quiz
      </Link>
    </section>
  );
}
