import React, { useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { useNavigate } from "react-router-dom";
import { v4 as uuidv4 } from "uuid";
import ROUTES from "../app/routes";
import { addQuiz } from "../features/quizzes/quizzesSlice"; // Import the addQuiz action
import { addCard } from "../features/cards/cardsSlice"; // Import the addCard action
import { selectTopics } from "../features/topics/topicsSlice"; // Import the selector to get topics

export default function NewQuizForm() {
  const [name, setName] = useState("");
  const [cards, setCards] = useState([]);
  const [topicId, setTopicId] = useState("");
  const navigate = useNavigate();
  const topics = useSelector(selectTopics); // Use the selector to get topics
  const dispatch = useDispatch();

  const handleSubmit = (e) => {
    e.preventDefault();
    if (name.length === 0 || topicId === "") {
      return;
    }

    const cardIds = [];

    // Dispatch addCard for each card and store the IDs in cardIds
    cards.forEach((card) => {
      const cardId = uuidv4();
      cardIds.push(cardId);
      dispatch(addCard({ id: cardId, front: card.front, back: card.back }));
    });

    const quizId = uuidv4();

    // Dispatch the addQuiz action with quiz data
    dispatch(addQuiz({ id: quizId, name, topicId, cardIds }));

    // Redirect to the quizzes page
    navigate(ROUTES.quizzesRoute());
  };

  const addCardInputs = (e) => {
    e.preventDefault();
    setCards(cards.concat({ front: "", back: "" }));
  };

  const removeCard = (e, index) => {
    e.preventDefault();
    setCards(cards.filter((_, i) => index !== i));
  };

  const updateCardState = (index, side, value) => {
    const newCards = cards.slice();
    newCards[index][side] = value;
    setCards(newCards);
  };

  return (
    <section>
      <h1>Create a new quiz</h1>
      <form onSubmit={handleSubmit}>
        <input
          id="quiz-name"
          value={name}
          onChange={(e) => setName(e.currentTarget.value)}
          placeholder="Quiz Title"
        />
        <select
          id="quiz-topic"
          value={topicId}
          onChange={(e) => setTopicId(e.currentTarget.value)}
          required
        >
          <option value="">Select Topic</option>
          {Object.values(topics).map((topic) => (
            <option key={topic.id} value={topic.id}>
              {topic.name}
            </option>
          ))}
        </select>
        {cards.map((card, index) => (
          <div key={index} className="card-front-back">
            <input
              id={`card-front-${index}`}
              value={cards[index].front}
              onChange={(e) => updateCardState(index, "front", e.currentTarget.value)}
              placeholder="Front"
            />
            <input
              id={`card-back-${index}`}
              value={cards[index].back}
              onChange={(e) => updateCardState(index, "back", e.currentTarget.value)}
              placeholder="Back"
            />
            <button onClick={(e) => removeCard(e, index)} className="remove-card-button">
              Remove Card
            </button>
          </div>
        ))}
        <div className="actions-container">
          <button onClick={addCardInputs}>Add a Card</button>
          <button type="submit">Create Quiz</button>
        </div>
      </form>
    </section>
  );
}
