import React from "react";
import { useSelector } from "react-redux";
import { selectQuizzes } from "../quizzes/quizzesSlice"; // Adjust the import path as necessary
import { Link } from "react-router-dom";

export default function Topic({ topic }) {
  const quizzes = useSelector(selectQuizzes); // Use the selector to get quizzes

  return (
    <section>
      <h2>{topic.name}</h2>
      <ul>
        {topic.quizIds.map((quizId) => (
          <li key={quizId}>
            <Link to={`/quizzes/${quizId}`}>{quizzes[quizId]?.name || 'Unknown Quiz'}</Link>
          </li>
        ))}
      </ul>
    </section>
  );
}
