import React from "react";
import { useSelector } from "react-redux";
import { Link } from "react-router-dom";
import { selectQuizzes } from "./quizzesSlice"; // Adjust the import path as necessary

export default function Quizzes() {
  const quizzes = useSelector(selectQuizzes); // Use the selector to get quizzes

  return (
    <section>
      <h1>Quizzes</h1>
      <ul>
        {Object.values(quizzes).map((quiz) => (
          <li key={quiz.id}>
            <Link to={`/quizzes/${quiz.id}`}>{quiz.name}</Link>
          </li>
        ))}
      </ul>
    </section>
  );
}
