import React from "react";
import { useSelector } from "react-redux";
import { Link, useParams, Navigate } from "react-router-dom";
import ROUTES from "../../app/routes";
import { selectTopics } from "../../features/topics/topics."; // Import selector for topics
import { selectQuizzes } from "../../features/quizzes/quizzesSlice"; // Import selector for quizzes

export default function Topic() {
  const topics = useSelector(selectTopics); // Use selector to get topics from state
  const quizzes = useSelector(selectQuizzes); // Use selector to get quizzes from state
  const { topicId } = useParams();
  const topic = topics[topicId];

  // Redirect if topic is not found
  if (!topic) {
    return <Navigate to={ROUTES.topicsRoute()} replace />;
  }

  // Map quizzes associated with the topic
  const quizzesForTopic = topic.quizIds.map((quizId) => quizzes[quizId]);

  return (
    <section>
      <img src={topic.icon} alt="" className="topic-icon" />
      <h1>{topic.name}</h1>
      <ul className="quizzes-list">
        {quizzesForTopic.length > 0 ? (
          quizzesForTopic.map((quiz) => (
            <li className="quiz" key={quiz.id}>
              <Link to={ROUTES.quizRoute(quiz.id)}>{quiz.name}</Link>
            </li>
          ))
        ) : (
          <p>No quizzes available for this topic.</p> // Handle case with no quizzes
        )}
      </ul>
      <Link to={`/quizzes/new?topicId=${topicId}`} className="button center">
        Create a New Quiz
      </Link>
    </section>
  );
}
