import { createSlice } from '@reduxjs/toolkit';

// Initial state for quizzes, indexed by ID
const initialState = {
  quizzes: {}, // This will hold your quizzes indexed by ID
};

// Create the quizzes slice
const quizzesSlice = createSlice({
  name: 'quizzes',
  initialState,
  reducers: {
    // Reducer to add a quiz
    addQuiz(state, action) {
      const { id, name, cardIds } = action.payload;
      state.quizzes[id] = {
        id,
        name,
        cardIds: cardIds || [], // Initialize with an empty array if no cards
      };
    },
    // Additional reducers can be added here, e.g., removeQuiz, updateQuiz
  },
});

// Export actions
export const { addQuiz } = quizzesSlice.actions;

// Selector to get quizzes from state
export const selectQuizzes = (state) => state.quizzes.quizzes;

// Export the reducer
export default quizzesSlice.reducer;
