import React from "react";
import { useSelector } from "react-redux";
import { useParams } from "react-router-dom";
import { selectQuizzes } from "./quizzesSlice"; // Adjust the import path as necessary

export default function Quiz() {
  const { quizId } = useParams(); // Get the quizId from the URL
  const quizzes = useSelector(selectQuizzes); // Use the selector to get quizzes

  const quiz = quizzes[quizId]; // Find the quiz using the quizId

  if (!quiz) {
    return <div>Quiz not found</div>; // Handle case where quiz is not found
  }

  return (
    <section>
      <h1>{quiz.name}</h1>
      {/* Render additional quiz details here */}
      <p>Quiz ID: {quiz.id}</p>
      {/* More rendering logic based on the quiz data */}
    </section>
  );
}
