import { createSlice } from '@reduxjs/toolkit';

const initialState = {
  quizzes: {}, // This will hold your quizzes indexed by ID
};

const quizzesSlice = createSlice({
  name: 'quizzes',
  initialState,
  reducers: {
    addQuiz(state, action) {
      const { id, name, cardIds } = action.payload;
      state.quizzes[id] = {
        id,
        name,
        cardIds: cardIds || [], // Initialize with an empty array if no cards
      };
    },
    // Other reducers like removeQuiz, updateQuiz can be added here
  },
});

// Export actions
export const { addQuiz } = quizzesSlice.actions;

// Selector to get quizzes
export const selectQuizzes = (state) => state.quizzes.quizzes;

// Export the reducer
export default quizzesSlice.reducer;
